    return (
        <>
            <Form onSubmit={() => {handleSubmit(); createQuestion(questionText, "freetext");}}>
                <Form.Group className="mb-3"    >
                    <Form.Label>Question:</Form.Label>
                    <Form.Control onChange={handleInput} placeholder="Write your question here and click 'Save'" />
                    <Form.Text className="text-muted">
                        Answer type will be a free text multi-line answer.
                    </Form.Text>
                </Form.Group>
                <Button variant="primary" type="submit">Save</Button>
                {/* <CreateSurvey text={questionText} /> */}
            </Form>
        </>
    )
};

export default AddFreetext;
