import React, { useState } from "react";
import Sidebar from '../components/Sidebar.js'

import { useSurveyContext } from '../contexts/SurveyContext.js';
import "./results.css"

import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';


const AnswerSurvey = () => {

    const { selectedSurvey } = useSurveyContext();
    const questions = selectedSurvey.questions;
    console.log(questions);
    
    const [input, setInput] = useState("");

    const handleInput = (event) => {
        setInput(event.target.value);
    };

    const handleFreetextSubmit = (event) => {
        event.preventDefault();

    }


/**
    const surveyQuestions = questions.map((question, index) => {
        if (question.questionType === "freetext") {
            return <div key={index}>
                <Form onSubmit={handleFreetextSubmit}>
                    <Form.Group className="mb-3">
                        <Form.Label>{question.questionTitle}:</Form.Label>
                        <Form.Control onChange={handleInput}
                            value={input}
                            placeholder="Write your answer here" />
                        <Form.Text className="text-muted"></Form.Text>
                    </Form.Group>
                    <Button variant="primary" type="submit">Save answer</Button>
                </Form>
                </div>;
        } else if (question.questionType === "dropdown") {
            return <h4>Dropdowv here</h4>
        } else return <h4>monivalinnat</h4>
    })
    )
     */




    return (
        <>
            <Container fluid className="mt-5">
                <Row className="mr-100">
                    <Col md="auto" className="mx-5">
                        <Sidebar />
                    </Col>
                    <Col className="text-center resultsSheet">
                        <h5>Here you can answer the <i>{selectedSurvey.surveyTitle}</i> survey</h5>
                        <p><b>Description of the survey:</b> {selectedSurvey.surveyDescription}</p>
                        {}
                    </Col>
                </Row>
            </Container>
        </>

    )
};

export default AnswerSurvey
