import React, { useState, useEffect, useContext } from "react";
import useSurveyContext from "../contexts/SurveyContext.js"
import axios from "axios";

import 'bootstrap/dist/css/bootstrap.min.css';
import ListGroup from 'react-bootstrap/ListGroup';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';


const Sidebar = () => {

    const survData = useContext;

    const [surveysData, setSurveysData] = useState([])

    useEffect(() => {
        const allSurveysData = async () => {
            const surveys = await axios.get("http://localhost:3001/surveys");

            setSurveysData(surveys.data);
        };
        allSurveysData();

    }, []);

    const handleClick = (survey) => {
        console.log(survey);
        survData.setSurveyData(survey); 
    };
   
    const surveyListDOM = surveysData.map(survey =>
        <ListGroup key={survey.id} variant="flush">
            <ListGroup.Item action onClick={() => handleClick(survey)}>
                {survey.surveyTitle}
            </ListGroup.Item>
        </ListGroup>
    )

    return (
        <Container className="mt-5">
            <Row>
                <Col xs={3}>
                    <h5><i>List of available surveys:</i></h5>
                    {surveyListDOM}
                </Col>
            </Row>
        </Container>

    )
}

export default Sidebar;
