import 'bootstrap/dist/css/bootstrap.min.css';
import './createSurvey.css';

import AddInput from '../components/AddInput.js';
import AddDropdown from '../components/AddDropdown.js';
import AddFreetext from '../components/AddFreetext.js';
import Sidebar from '../components/Sidebar.js'

import { useSurveyContext } from "../contexts/SurveyContext.js";

import Container from 'react-bootstrap/Container';
import Stack from 'react-bootstrap/Stack';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ListGroup from 'react-bootstrap/ListGroup';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';

import { Trash, Pencil } from 'react-bootstrap-icons';

import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';
import { ListGroupItem } from 'react-bootstrap';

const CreateSurvey = () => {

    const { getNewId, addSurvey, selectedSurvey } = useSurveyContext();

    const [surveyTitle, setSurveyTitle] = useState("");
    const [surveyDescription, setSurveyDescription] = useState("");

    const [newSurvey, setNewSurvey] = useState({ surveyTitle: "", surveyDescription: "", questions: [], answers: [] });
    const [questionType, setQuestionType] = useState("");

    const [modifyIsActive, setModifyIsActive] = useState(false);

    const navigate = useNavigate();

    const handleClick = (type) => {
        setQuestionType(type);
    };

    const createSurveyTitle = (event) => {
        setSurveyTitle(event.target.value);
    };

    const createSurveyDescription = (event) => {
        setSurveyDescription(event.target.value);
    };

    const createQuestion = (questionTitle, dropdownQuestionOptions, questionType) => {
        const newQuestion = {
            questionTitle,
            dropdownQuestionOptions,
            questionType,
            questionId: getNewId()
        };
        setNewSurvey({
            ...newSurvey,
            surveyTitle: surveyTitle, surveyDescription: surveyDescription,
            questions: [...newSurvey.questions, newQuestion]
        });
    };

    const removeQuestion = (question) => {
        const remainingQuestions = newSurvey.questions.filter((item) => item.id !== question.id);
        setNewSurvey({
            ...newSurvey,
            questions: remainingQuestions
        });
    };

    const handleSave = (newSurvey) => {
        return modifyIsActive ? modifySurvey(newSurvey) : addNewSurvey(newSurvey)
    };

    const modifySurvey = (newSurvey) => {
        navigate("/thanks", { replace: true, state: `The modified survey has now been saved succesfully.` });
    };

    const addNewSurvey = (newSurvey) => {
        addSurvey(newSurvey);
        navigate("/thanks", { replace: true, state: `Thank you for creating the ${surveyTitle} survey! The survey has now been saved succesfully.` });
    };

    return (
        <>
            <Container className='mt-4 text-left'>
                <h5>To modify or delete an existing survey:</h5>
                <Button onClick={() => setModifyIsActive(true)} variant="outline-secondary">Modify/delete an existing survey</Button>{' '}
            </Container>

            <Container fluid className="mt-5">
                <Row>
                    {modifyIsActive ?
                        <Col sm={3} className="mx-5" style={{ backgroundColor: "ivory" }}>
                            <Sidebar modifyIsActive={modifyIsActive} />
                        </Col>
                        : <Col sm={2} className="mx-5" style={{ backgroundColor: "ivory" }}></Col>}
                    <Col sm={6} className='text-left mx-5' style={{ backgroundColor: "azure" }}>
                        {modifyIsActive ?
                            <div>
                                {selectedSurvey.surveyTitle ? <h4>Modify: <i>{selectedSurvey.surveyTitle}</i></h4>
                                    : <h4>Select a survey to modify from the left sidebar</h4>}
                            </div>
                            :
                            <h4>To create a question, choose what kind of a question do you want to use.
                                The question types are: dropdown, multiple choices and free text.</h4>}
                        <Container>
                            <Stack gap={3}>
                                <label>Give your survey a short and descriptive title:</label>
                                <ListGroup>
                                    <ListGroupItem className="list-group-item d-flex justify-content-between align-items-start mt-2">
                                        <div>
                                            
                                            <input onChange={createSurveyTitle} type="text" placeholder={selectedSurvey.surveyTitle} />
                                        </div>
                                        <OverlayTrigger key="edit" placement="top" overlay={<Tooltip id="edit">Edit</Tooltip>}>
                                            <Button size="sm" variant="light">
                                                <Pencil className='bi bi-pencil' size={20}></Pencil>
                                            </Button>
                                        </OverlayTrigger>
                                    </ListGroupItem>
                                    <label>Write a short description of the survey:</label>
                                    <textarea
                                        onChange={createSurveyDescription}
                                        name="surveyDescription"
                                        id="surveyDescription"
                                        cols="20" rows="3"
                                        placeholder={selectedSurvey.surveyDescription}></textarea>
                                </ListGroup>
                                {newSurvey.questions.map(question =>
                                    <div key={question.questionTitle}>
                                        <ListGroup>
                                            <ListGroup.Item className="list-group-item d-flex justify-content-between align-items-start mt-2">
                                                <div>
                                                    <p><b>Question {newSurvey.questions.indexOf(question) + 1}, </b> ({question.questionType}): {question.questionTitle}</p>
                                                    <p>{(question.dropdownQuestionOptions) ? `Dropdown options: ${question.dropdownQuestionOptions}`
                                                        : (null)}</p>
                                                </div>
                                                <ButtonGroup aria-label="Edit tools">
                                                    <OverlayTrigger key="edit" placement="top" overlay={<Tooltip id="edit">Edit</Tooltip>}>
                                                        <Button size="sm" variant="light">
                                                            <Pencil className='bi bi-pencil' size={20}></Pencil>
                                                        </Button>
                                                    </OverlayTrigger>
                                                    <OverlayTrigger key="delete" placement="top"
                                                        overlay={<Tooltip id="delete">Delete</Tooltip>}>
                                                        <Button onClick={() => removeQuestion(question)} size="sm" variant="light">
                                                            <Trash className='bi bi-trash' size={20}></Trash>
                                                        </Button>
                                                    </OverlayTrigger>
                                                </ButtonGroup>
                                            </ListGroup.Item>
                                        </ListGroup>
                                    </div>
                                )}
                                <p>Choose what kind of a question to add:</p>

                                <ButtonGroup gap={1} aria-label="Basic example">
                                    <Button onClick={() => handleClick("dropdown")} variant="outline-dark">Dropdown + </Button>
                                    <Button onClick={() => handleClick("input")} variant="outline-dark">Multiple choice + </Button>
                                    <Button onClick={() => handleClick("freetext")} variant="outline-dark">Free text + </Button>
                                </ButtonGroup>
                                <div>
                                    {(questionType === "dropdown") ? <AddDropdown createQuestion={createQuestion} /> : null}
                                    {(questionType === "input") ? <AddInput /> : null}
                                    {(questionType === "freetext") ? <AddFreetext createQuestion={createQuestion} /> : null}
                                </div>
                                <button onClick={handleSave} type="submit">Submit survey</button>
                            </Stack>
                        </Container>
                    </Col>
                </Row>
            </Container>
        </>
    )
};
export default CreateSurvey;
