import Sidebar from '../components/Sidebar.js'
import { useSurveyContext } from '../contexts/SurveyContext.js';
import "./results.css"

import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';

import { Trash, Pencil } from 'react-bootstrap-icons';


const Results = () => {

    const { selectedSurvey } = useSurveyContext();
    const { allSurveys } = useSurveyContext();

    const surveyList = allSurveys.map(survey =>
        <Container fluid className="mt-3">
            <Row className="justify-content-md-center">
                <Col xs={4} className="text-left">
                    <ListGroup key={survey.id} variant="flush">
                        <ListGroup.Item as="li">
                            <h5>{survey.surveyTitle}</h5>
                            <ButtonGroup aria-label="Edit tools">
                        <OverlayTrigger key="edit" placement="top" overlay={
                            <Tooltip id="edit">Edit</Tooltip>
                        }>
                            <Button size="sm" variant="light">
                                <Pencil className='bi bi-pencil' size={20}></Pencil>
                            </Button>
                        </OverlayTrigger>
                        <OverlayTrigger key="delete" placement="top"
                            overlay={
                                <Tooltip id="delete">Delete</Tooltip>
                            }>
                            <Button size="sm" variant="light">
                                <Trash className='bi bi-trash' size={20}></Trash>
                            </Button>
                        </OverlayTrigger>
                    </ButtonGroup>
                        </ListGroup.Item>
                    </ListGroup>
                </Col>
                <Col xs={1} className="text-center">
                    
                </Col>
            </Row>
        </Container>
    )


    return (
        <Container className="mt-5">
            <Row className="justify-content-md-center">
                <Col>
                    <h4>Here is the list of available surveys. Use the icons to delete or modify a survey</h4>
                    {surveyList}

                </Col>
            </Row>
        </Container>

    )
};

export default Results;
