import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import ListGroup from 'react-bootstrap/ListGroup';
import Results from "/Results.js"

const SurveyContext = createContext({});

const Sidebar = () => {

    const [survey, setSurvey] = useState({})
    const [surveyData, setSurveyData] = useState([])

    useEffect(() => {        
        const surveysData = async () => {
            const surveys = await axios.get("http://localhost:3001/surveys");     
            
            setSurveyData(surveys);
            console.log(surveys);
            
        }
        surveysData();
        
    }, []);

    const handleClick = (survey) => {
        setSurvey(survey)
    }

    const surveyListDOM = surveyData.map(survey =>
        <ListGroup variant="flush">
            <ListGroup.Item action onClick={() => handleClick(survey)}>{survey.surveyTitle}</ListGroup.Item>
        </ListGroup>    
        )

    return (
        <div>
        <SurveyContext.Provider value={survey}>
            <Results />
        </SurveyContext.Provider> 
        <Container> 
            <Row>List of available surveys:</Row>
            {surveyListDOM}
        </Container>
        </div>

    )
}

export default Sidebar;
