import React, { useState } from "react";
import Form from 'react-bootstrap/Form';
import { Button } from 'react-bootstrap';


const AddFreetext = (props) => {
    
    const [input, setInput] = useState("");
            
    const handleInput = (event) => {
        setInput(event.target.value);        
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        props.createQuestion(input, "",  "freetext");
    };

    const clearInput = (event) => {
        event.preventDefault();
        event.target.form.elements.input.value = "";
    };
        
    return (
        <>
            <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3">
                    <Form.Label>Free text question:</Form.Label>
                    <Form.Control onChange={handleInput} placeholder="Write your question here and click 'Save'" name="input" />
                    <InputGroup>
                            <FormControl onChange={handleDropdownOption}
                                placeholder='Add question option'
                                aria-label='Add question option'
                                aria-describedby='basic-addon2'
                            />
                            <Button onClick={saveDropdown} variant="outline-secondary" id="button-addon2" type="button">
                                +
                            </Button>
                        </InputGroup>
                    <Form.Text className="text-muted">
                        Answer type will be a free text multi-line answer.
                    </Form.Text>
                </Form.Group>
                <Button onClick={clearInput} variant="primary" type="submit">Save question</Button>
            </Form>
        </>
    )
};

export default AddFreetext;
