import React, { useState } from "react";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import InputGroup from 'react-bootstrap/InputGroup';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';
import Stack from 'react-bootstrap/Stack';

const AddInput = (props) => {

    const [questionInput, setQuestionInput] = useState("");
    const [optionInput, setOptionInput] = useState("");
    const [optionArray, setOptionArray] = useState([]);

    const handleQuestionInput = (event) => {
        setQuestionInput(event.target.value)
    };

    const handleOptionInput = (event) => {
        setOptionInput(event.target.value)
    };

    const saveOption = () => {
        let options = optionArray.concat(optionInput);
        setOptionArray(options);
        setOptionInput("")
    };

    const handleQuestionReady = () => {
        props.createQuestion(questionInput, optionArray, "multiple-choice");

        setQuestionInput("");
        setOptionArray([]);
    }

    return (
        <>
            <Form>
                <Form.Group className="mb-3">
                    <Form.Label>Question:</Form.Label>
                    <InputGroup className="mb-3">
                        <Form.Control onChange={handleQuestionInput}
                            value={questionInput}
                            placeholder="Write your question here" />
                        <Form.Label>Add new multiple-choice option:</Form.Label>
                        <Form.Control onChange={handleOptionInput}
                            value={optionInput}
                            placeholder="Write an option here"
                            aria-describedby='basic-addon2'
                        />
                        <OverlayTrigger key="edit" placement="top" overlay={<Tooltip id="edit">Save and add new</Tooltip>}>
                            <Button onClick={saveOption} variant="outline-secondary" id="button-addon2" type="button">
                                +
                            </Button>
                        </OverlayTrigger>
                    </InputGroup>
                </Form.Group>
                <Stack className="text-center" style={{ marginRight: "200px", marginLeft: "200px" }}>
                    <Button  onClick={handleQuestionReady}>Question ready</Button>
                </Stack>
            </Form>
        </>
    )
};

export default AddInput
