import React, { useState, useEffect } from "react";
import useSurveyContext from "../contexts/SurveyContext.js"
import axios from "axios";

import 'bootstrap/dist/css/bootstrap.min.css';
import ListGroup from 'react-bootstrap/ListGroup';


const Sidebar = () => {

    const surveyData = useSurveyContext();

    const [surveysData, setSurveysData] = useState([])

    useEffect(() => {        
        const allSurveysData = async () => {
            const surveys = await axios.get("http://localhost:3001/surveys");     
            
            setSurveysData(surveys.data);
            console.log(surveys.data);
            
        }
        allSurveysData();
        
    }, []);

    const handleClick = (survey) => {
        surveyData.setSurveyData(survey)
    }

    const surveyListDOM = surveysData.map(survey =>
        <ListGroup key={survey.id} variant="flush">
            <ListGroup.Item action onClick={() => handleClick(survey)}>
                {survey.surveyTitle}
            </ListGroup.Item>
        </ListGroup>          
        )

    return (
        <div>
            <h5>List of available surveys:</h5>
            {surveyListDOM}
        </div>

    )
}

export default Sidebar;
