import { InputGroup, FormControl } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import Container from 'react-bootstrap/Container';
import Stack from 'react-bootstrap/Stack';
import Form from 'react-bootstrap/Form';

import React, { useState } from "react";


const AddDropdown = (props) => {

    const [questionInput, setQuestionInput] = useState("");
    const [dropdownOption, setDropdownOption] = useState("");
    const [dropDown, setDropDown] = useState([]);

    const handleQuestionInput = (event) => {
        setQuestionInput(event.target.value);
        console.log(questionInput);
    };

    const handleDropdownOption = (event) => {
        setDropdownOption(event.target.value);
        console.log(dropdownOption);
        let dropDownInput = dropDown.concat(drop)
    };
    const handleSubmit = (event) => {
        event.preventDefault();
        props.createQuestion(questionInput, dropDown, "dropdown");

    };

        return (
            <>
                <Container>
                    <Stack gap={2} className='col-sm-6 mx-auto'>
                        <Form.Control onChange={handleQuestionInput} type="text" placeholder='Write your question here' />
                        <InputGroup>
                            <FormControl onChange={handleDropdownOption}
                                placeholder='Add question option'
                                aria-label='Add question option'
                                aria-describedby='basic-addon2'
                            />
                            <Button  variant="outline-secondary" id="button-addon2">
                                +
                            </Button>

                        </InputGroup>
                        <Button onClick={handleSubmit} variant="primary" type="submit">Save</Button>

                    </Stack>
                </Container>
            </>
        )
    };

    export default AddDropdown;
