import React, { useState } from "react";
import Sidebar from '../components/Sidebar.js'
import { useNavigate } from 'react-router-dom';

import { useSurveyContext } from '../contexts/SurveyContext.js';

import 'bootstrap/dist/css/bootstrap.min.css';

import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Dropdown from 'react-bootstrap/Dropdown';
import ButtonGroup from 'react-bootstrap/ButtonGroup';


const AnswerSurvey = () => {

    const { selectedSurvey } = useSurveyContext();
    const { addAnswers } = useSurveyContext();

    const [input, setInput] = useState("");
    const [newAnswers, setNewAnswers] = useState([]);
    const [dropdownOption, setDropdownOption] = useState("");

    const navigate = useNavigate();

    const handleInput = (event) => {
        setInput(event.target.value);
    };

    const handleFreetextSubmit = (question, event) => {
        event.preventDefault();
        const answer = {
            id: question.questionId,
            type: question.questionType,
            answ: input
        };
        setNewAnswers(newAnswers.concat(answer));
    };
    const handleDropdown = (key, question, event) => {
        const answer = {
            id: question.questionId,
            answ: option
        };
        setNewAnswers(newAnswers.concat(answer));
        setDropdownOption(event.target.value);
        };
        console.log(dropdownOption);
        

    const handleSave = () => {
        const surveyId = selectedSurvey.id;
        addAnswers(newAnswers, surveyId);
        navigate("/thanks", { replace: true, state: `Thank you for answering the ${selectedSurvey.surveyTitle} survey! Your answers have now been saved succesfully. ` });
    };
    let number = 0;
    const listSurveyQuestions = selectedSurvey.questions ? selectedSurvey.questions.map((question) => {
        if (question.questionType === "freetext") {
            number += 1;
            return <div key={question.id}>
                <Container className="mb-4">
                    <h6>{number}.</h6>
                    <Form onSubmit={(event) => handleFreetextSubmit(question, event)}>
                        <Form.Group  className="mb-3">
                            <Form.Label ><h5>{question.questionTitle}</h5></Form.Label>
                            <Form.Control
                                onChange={handleInput}
                                placeholder="Write your answer here" />
                            <Form.Text></Form.Text>
                        </Form.Group>
                        <Button variant="secondary" type="submit">Save answer</Button>
                    </Form>
                </Container>
            </div>

        } else if (question.questionType === "dropdown") {
            number += 1;
            return <div key={question.id}>
                <Container className="mb-4">
                    <h6>{number}.</h6>
                    <h5>{question.questionTitle}</h5>
                    <Dropdown className="mb-2" as={ButtonGroup}>
                        <Button variant="light">{dropdownOption ? <h6>Your answer: </h6> : <h6>Answer options</h6>}</Button>
                        <Dropdown.Toggle onSelect={handleDropdown} variant="secondary" id="dropdown-split-basic" />
                        <Dropdown.Menu>
                            {question.dropdownQuestionOptions.map((option, index) => (
                                <Dropdown.Item key={index} eventKey={option.key}>{option}</Dropdown.Item>
                            ))}
                        </Dropdown.Menu>
                    </Dropdown>
                </Container>
            </div>

        } else return <h4>monivalinnat</h4>
    }) : (null);

    return (
        <>
            <Container fluid className="mt-5 mb-5">
                <Row className="mr-100">
                    <Col md="auto" className="mx-5">
                        <Sidebar />
                    </Col>
                    {selectedSurvey.surveyTitle ?
                        <Col xs={7} className="text-left">
                            <div className="mb-5">
                                <h4><i>{selectedSurvey.surveyTitle}</i></h4>
                                <br/>
                                <p><b>Description of the survey:</b> {selectedSurvey.surveyDescription}</p>
                            </div>
                            <div>
                                {listSurveyQuestions}
                                <button onClick={handleSave} type="submit">Submit Answers</button>
                            </div>
                        </Col>
                        : <Col className="text-center"><h5>First, choose a survey from the left sidebar to give your answer.</h5></Col>}
                </Row>
            </Container>
        </>
    )
};
export default AnswerSurvey;
