import React, { useContext, useEffect, useState } from "react";
import { DataContext } from "../contexts/DataContext.js";

const CreateSurveyID = () => {

    const SurveyData = useContext(DataContext);

    const [surveysData, setSurveysData] = useState([]);

    useEffect(() => {
        const allSurveysData = async () => {
            const surveys = await SurveyData.getAllSurveys();

            setSurveysData(surveys);           
        };
        allSurveysData();
    }, []);

    let surveyID = 1;

    const surveyIDs = surveysData.map((survey) => survey.id);
    while (true) {
        if (surveyIDs.includes(surveyID)) {
            surveyID += 1;
        } else {
            return surveyID
        };
    };
};

export default CreateSurveyID
