import React, { useState } from "react";

import { useSurveyContext } from '../contexts/SurveyContext.js';

import "./results.css"
import 'bootstrap/dist/css/bootstrap.min.css';

import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';
import Tooltip from 'react-bootstrap/Tooltip';
import Modal from 'react-bootstrap/Modal';

import { Trash, Pencil } from 'react-bootstrap-icons';


const Results = () => {

    const { allSurveys, selectedSurvey, setSelectedSurvey, deleteSurvey } = useSurveyContext();

    const [show, setShow] = useState(false)

    const handleDelete = (survey) => {
        deleteSurvey(survey.id)
    };

    const handleClose = () => setShow()

    const surveyList = allSurveys.map(survey =>
        <ListGroup key={survey.id} variant="flush">
            <ListGroup.Item className="list-group-item d-flex justify-content-between align-items-start mt-2">
                <h5>{survey.surveyTitle}</h5>
                <ButtonGroup aria-label="Edit tools">
                    <OverlayTrigger key="edit" placement="top" overlay={<Tooltip id="edit">Edit</Tooltip>}>
                        <Button onClick={() => setSelectedSurvey(survey)} size="sm" variant="light">
                            <Pencil className='bi bi-pencil' size={20}></Pencil>
                        </Button>
                    </OverlayTrigger>
                    <OverlayTrigger key="delete" placement="top" overlay={<Tooltip id="delete">Delete</Tooltip>}>
                        <Button onClick={() => setShow(true)} size="sm" variant="light">
                            <Trash className='bi bi-trash' size={20}></Trash>
                        </Button>
                        <Modal
                            show={show}
                            onHide={handleClose}
                            backdrop="static"
                            keyboard={false}
                        >
                            <Modal.Header closeButton>
                                <Modal.Title>Delete {selectedSurvey.surveyTitle}</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>Are you sure you wan't to delete the survey?</Modal.Body>
                            <Modal.Footer>
                                <Button variant="danger" onClick={handleDelete}> Yes, delete!</Button>
                                <Button variant="secondary" onClick={() => setShow(false)}>No, go back</Button>
                            </Modal.Footer>
                        </Modal>
                    </OverlayTrigger>
                </ButtonGroup>
            </ListGroup.Item>
        </ListGroup>
    )


    return (
        <>
            {selectedSurvey.surveyTitle ?
                <Container className="mt-5">
                    <Row className="justify-content-md-center">
                        <Col xs={8}>
                            <p>Modify survey: <i>{selectedSurvey.surveyTitle}</i>survey</p>
                            <Button>Go back</Button>
                        </Col>
                    </Row>
                </Container>
                :
                <Container className="mt-5">
                    <Row className="justify-content-md-center">
                        <Col xs={8}>

                            <h4>Here is the list of available surveys. Use the icons to delete or modify a survey.</h4>
                            <br />
                            {surveyList}
                        </Col>
                    </Row>
                </Container>
            }
        </>
    )
};

export default Results;
