import React, { useState } from "react";
import Sidebar from '../components/Sidebar.js'

import { useSurveyContext } from '../contexts/SurveyContext.js';
import "./results.css"

import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Dropdown from 'react-bootstrap/dropdown';


const AnswerSurvey = () => {

    const { selectedSurvey } = useSurveyContext();
    const { addAnswers } = useSurveyContext();

    const [input, setInput] = useState("");
    const [allAnswers, setAllAnswers] = useState([])

    const handleInput = (event) => {
        setInput(event.target.value);
    };

    const handleFreetextSubmit = (question, event) => {
        event.preventDefault();
        const answer = {
            id: question.questionId,
            type: question.questionType,
            answ: input
        };
        setAllAnswers(allAnswers.concat(answer));
    };


    const handleDropDown = (option, id, type) => {
        const answer = {
            id: id,
            type: type,
            answ: option
        };
        setAllAnswers(allAnswers.concat(answer));
    };
    const handleSave = () => {
        const surveyId = selectedSurvey.id;
        addAnswers(allAnswers, surveyId);        
    };

    const listSurveyQuestions = selectedSurvey.questions ? selectedSurvey.questions.map((question) => {
        if (question.questionType === "freetext") {
            return <Container key={question.id} className="mb-5">
                <Form onSubmit={(event) => handleFreetextSubmit(question, event)}>
                    <Form.Group className="mb-3">
                        <Form.Label><h5>{question.questionTitle}</h5></Form.Label>
                        <Form.Control onChange={handleInput}
                            placeholder="Write your answer here" />
                        <Form.Text></Form.Text>
                    </Form.Group>
                    <Button variant="secondary" type="submit">Save answer</Button>
                </Form>
            </Container>;

        } else if (question.questionType === "dropdown") {
            return <Container key={question.id} className="mb-5">
                <h5>{question.questionTitle}</h5>
                <Dropdown>
                    <Dropdown.Toggle variant="secondary" id="dropdown-basic">
                        Answer options
                    </Dropdown.Toggle>

                    <Dropdown.Menu>
                        {question.dropdownQuestionOptions.map((option) => (
                            <Dropdown.Item onClick={() => handleDropDown(option, question.questionId, question.questionType)}>{option}</Dropdown.Item>
                        ))}
                    </Dropdown.Menu>
                </Dropdown>
            </Container>
        } else return <h4>monivalinnat</h4>
    }) : (null);

    return (
        <>
            <Container fluid className="mt-5 mb-5">
                <Row className="mr-100">
                    <Col md="auto" className="mx-5">
                        <Sidebar />
                    </Col>
                    {selectedSurvey.surveyTitle ?
                        <Col className="text-left resultsSheet">
                            <h4>Here you can answer the <i>{selectedSurvey.surveyTitle}</i> survey</h4>
                            <p><b>Description of the survey:</b> {selectedSurvey.surveyDescription}</p>
                            {listSurveyQuestions}
                        <button onClick={handleSave} type="submit">Submit Answers</button>    
                        </Col>
                        : <Col className="text-center"><h5>First, choose a survey from the left sidebar to give your answer.</h5></Col>}
                </Row>
            </Container>
        </>

    )
};

export default AnswerSurvey
