import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import SurveyContext from "../contexts/SurveyContext.js"

import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import ListGroup from 'react-bootstrap/ListGroup';

const Sidebar = () => {

    const { survey, setSurvey } = useContext(SurveyContext);
    const [surveyData, setSurveyData] = useState([])

    useEffect(() => {        
        const surveysData = async () => {
            const surveys = await axios.get("http://localhost:3001/surveys");     
            
            setSurveyData(surveys);
        }
        surveysData();
        
    }, []);

    const handleClick = (survey) => {
        setSurvey(survey)
    }

    const surveyList = surveyData.map(survey =>
        <ListGroup variant="flush">
            <ListGroup.Item action onClick={() => handleClick(survey)}>{survey.surveyTitle}</ListGroup.Item>
        </ListGroup>    
        )

    return (
        <Container>
            <Row>List of available surveys</Row>
            {surveyList}
        </Container>
    )
}

export default Sidebar;
