import React, { useState } from "react";
import { Form } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import { useTextContext } from "../contexts/TextContext.js";


const AddInput = () => {

    const { setQuestionText } = useTextContext();

    const [input, setInput] = useState("");

    const handleInput = (event) => {
        setInput(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        setQuestionText(input);
    }

    return (
        <>
            <Form onSubmit={() => setQuestionText(input)}>
                <Form.Group className="mb-3"    >
                    <Form.Label>Question:</Form.Label>
                    <Form.Control onChange={handleInput} placeholder="Write your question here and click 'Save'" />
                    <Form.Text className="text-muted">
                        Answer type will be a free text multi-line answer.
                    </Form.Text>
                </Form.Group>
                <Button variant="primary" type="submit">Save</Button>
            </Form>
        </>
    )
};


export default AddInput
