import {Chart as ChartJS,CategoryScale,LinearScale,BarElement,Title,Tooltip,Legend,} from 'chart.js';
import { Bar } from 'react-chartjs-2';

import { useSurveyContext } from '../../contexts/SurveyContext.js';

ChartJS.register(CategoryScale,LinearScale,BarElement,Title,Tooltip,Legend);
  
const BarChart = (props) => {

    const { selectedSurvey } = useSurveyContext();

    const surveyAnswers = selectedSurvey.answers ? selectedSurvey.answers.map(a => a[props.question]) : (null);
    console.log();
    
    const answerLabels = [...new Set(surveyAnswers)];      
    const answerCounts = answerLabels.map(label => {
        const filtered = surveyAnswers.filter(answer => answer === label);
        return filtered.length;
      });    
    const labels = answerLabels; 

    const data = {
        labels,
        datasets: [
          {
            label: 'answers',
            data: answerCounts,
            backgroundColor: 'rgba(55, 99, 132, 1)',
            borderColor: 'rgb(55, 99, 132, 1)'
          },
        ],
      };   
  return (
    <>
       <Bar data={data} />
    </>
  )
};

export default BarChart;
