import React, { createContext, useState, useContext } from "react";

export const SurveyContext = createContext(undefined);

const SurveyContextProvider = (props) => {

    const [surveyData, setSurveyData] = useState({});

    return (
        <SurveyContext.Provider value={{surveyData, setSurveyData}}>
            {props ? props.children : (null)}
        </SurveyContext.Provider>
    )
};

export const useSurveyContext = () => {
    const surveyData = useContext(SurveyContext);
    if (!surveyData) throw new Error("must be used within it's provider")
    return surveyData
}

export default SurveyContextProvider;
