import { InputGroup, FormControl } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import Container from 'react-bootstrap/Container';
import Stack from 'react-bootstrap/Stack';
import Form from 'react-bootstrap/';

import React, { useState } from "react";
import { ChatLeftQuote } from 'react-bootstrap-icons';


const AddDropdown = () => {

    const [questionInput, setQuestionInput] = useState("");

    const handleInput = (event) => {
        setQuestionInput(event.target.value);
        console.log(questionInput);
        
    };
        return (
            <>
                <Container>
                    <Stack gap={2} className='col-sm-6 mx-auto'>
                        <Form.Control onChange={handleInput} type="text" placeholder='Write your question here' />
                        <InputGroup>
                            <FormControl 
                                placeholder='Add question option'
                                aria-label='Add question option'
                                aria-describedby='basic-addon2'
                            />
                            <Button variant="outline-secondary" id="button-addon2">
                                +
                            </Button>

                        </InputGroup>
                        <Button variant="primary" type="submit">Save</Button>

                    </Stack>
                </Container>
            </>
        )
    };

    export default AddDropdown;
