import React, { useContext, useState, useEffect } from "react";
import useSurveyContext from "../contexts/SurveyContext.js"
import axios from "axios";

import 'bootstrap/dist/css/bootstrap.min.css';
import Row from 'react-bootstrap/Row';
import ListGroup from 'react-bootstrap/ListGroup';
import Results from "/Results.js";


const Sidebar = () => {

    const survey = useContext(useSurveyContext);

    const [surveyData, setSurveyData] = useState([])

    useEffect(() => {        
        const allSurveysData = async () => {
            const surveys = await axios.get("http://localhost:3001/surveys");     
            
            setSurveyData(surveys);
            console.log(surveys);
            
        }
        allSurveysData();
        
    }, [survey]);

    const handleClick = (survey) => {
        setSurveyData(survey)
    }

    const surveyListDOM = surveyData.map(survey =>
        <ListGroup>
            <ListGroup.Item action onClick={handleClick}>
                {survey.surveyTitle}
            </ListGroup.Item>
        </ListGroup>

           
        )

    return (
        <div>
            <h4>List of available surveys:</h4>
            {surveyListDOM}
        </div>

    )
}

export default Sidebar;
