import React from "react";
import { Link } from "react-router-dom";
import { ReactComponent as CreateLogo } from '../pictures/createLogo.svg';
import { ReactComponent as AnswerLogo } from '../pictures/answerLogo.svg';
import { ReactComponent as ResultsLogo } from '../pictures/resultsLogo.svg';
import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';



const LandingPage = () => {

  return (
    <div>
      <Container>
        <Row className="jtext-center text-md-right"> 
      <h1>LetÂ´s visualize survey data!</h1>
      <h2>Create a survey, answer surveys and visualize your survey data on these pages!</h2>
      </Row>
      </Container>
      <Container fluid="xl">
        <Row className="justify-content-md-center">
          <Col xs={2} className="text-center text-md-right">
              <Link to="/answer">
                <h3>Create a survey</h3>
                <CreateLogo />
                <p>If you want to create a new survey, this is the place to go next.</p>
              </Link> 
          </Col>

          <Col xs={2} className="text-center text-md-right">
              <Link to="/answer">
                <h3>Answer a survey</h3>
                <AnswerLogo />
                <p>Here you can find all surveys and give your answer.</p>
              </Link>
          </Col>

          <Col xs={2} className="text-center text-md-right">
            <Link to="/results">
              <h3>See the results</h3>
              <ResultsLogo />
              <p>To view some results, go here!</p>
            </Link>
          </Col>

        </Row>
      </Container>
    </div>
  );
};

export default LandingPage;
