import React from "react";
import Sidebar from '../components/Sidebar.js'

import { useSurveyContext } from '../contexts/SurveyContext.js';
import "./results.css"

import 'bootstrap/dist/css/bootstrap.min.css';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Form from 'react-bootstrap/Form';


const AnswerSurvey =  () => {
    
    const { selectedSurvey } = useSurveyContext();

    const handleFreetext = (eve)

   const surveyQuestions = selectedSurvey.questions.map((question) => {
       if (question.questionType === "freetext") {
    
        <Form onSubmit={handleFreetext}>
        <Form.Group className="mb-3">
            <Form.Label>Free text question:</Form.Label>
            <Form.Control onChange={handleInput}
                value={input}
                placeholder="Write your question here and click 'Save'" />
            <Form.Text className="text-muted">
                Answer type will be a free text multi-line answer.
            </Form.Text>
        </Form.Group>
        <Button variant="primary" type="submit">Save question</Button>
    </Form>
           
           

       }
   })
    

    
    
    return (
        <>
            <Container fluid className="mt-5">
                <Row className="mr-100">
                    <Col md="auto" className="mx-5">
                        <Sidebar />
                    </Col>
                    <Col className="text-center resultsSheet">
                        <h5>Here you can answer the <i>{selectedSurvey.surveyTitle}</i> survey</h5>
                        <p><b>Description of the survey:</b> {selectedSurvey.surveyDescription}
                        </p>
                        {surveyQuestions}
                    </Col>
                </Row>
            </Container>
        </>

    )
};

export default AnswerSurvey
